using namespace std;
int MOD = 20170805;
int cache[500][500];
// 전역 변수를 정의할 경우 함수 내에 초기화 코드를 꼭 작성해주세요.
int solution(int m, int n, vector<vector<int>> city_map) {
	int answer = 0;
	for (int i = 0; i < n; i++) {
		if (city_map[0][i] == 1) {
			break;
		}
		else {
			cache[0][i] = 1;
		}
	}
	for (int i = 0; i < m; i++) {
		if (city_map[i][0] == 1) {
			break;
		}
		else {
			cache[i][0] = 1;
		}
	}

	for (int i = 1; i < m; i++) {
		for (int j = 1; j < n; j++) {
			if (city_map[i][j] != 1) {
				//왼쪽,오른쪽 둘다2인지 먼저 판단
				if (city_map[i - 1][j] == 2 && city_map[i][j - 1] == 2) {
					cache[i][j] = cache[i-2][j] % 20170805 + cache[i][j-2] % 20170805;
				}
				//위가 2
				else if (city_map[i - 1][j] == 2) {
					//위가 경계라면? 왼쪽에서의 경우의수만 가져온다
					if (i - 1 == 0) {
						cache[i][j] = cache[i][j - 1] % 20170805;
					}
					//위가 경계가 아니고 그냥 위만 2라면 위위에서의 경우의수 가져오고 왼쪽에서 경우의수 가져와서 더한당
					else {
						//왼쪽이 1이라면 왼쪽에서는 경우의수를 가져오면 안된다.
						if (city_map[i][j - 1] == 0) cache[i][j] = cache[i - 2][j] % 20170805;
						else { cache[i][j] = cache[i - 2][j] % 20170805 + cache[i][j - 1] % 20170805; }
					}


				}
				//왼쪽이 2
				else if (city_map[i][j-1] == 2) {
					//왼쪽이 경계라면? 위에서의 경우의수만 가져온다
					if (j - 1 == 0) {
						cache[i][j] = cache[i-1][j] % 20170805;
					}
					//왼쪽이 경계가 아니고 그냥 왼쪽만 2라면 왼왼에서의 경우의수 가져오고 위에서 경우의수 가져와서 더한당
					else {
						//위가 1이라면 위에서는 경우의수를 가져오면 안된다
						if (city_map[i - 1][j] == 0)cache[i][j] = cache[i][j - 2] % 20170805;
						else { cache[i][j] = cache[i][j - 2] % 20170805 + cache[i - 1][j] % 20170805; }
					}
				}
				//양쪽이 0이라면
				else {
					cache[i][j] = cache[i][j - 1] % 20170805 + cache[i - 1][j] % 20170805;
				}
			}
		}
	}
	return cache[m-1][n-1] % 20170805;
}
